sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
//Define the getEmail function
	getEmail: function() {
		//Create a glide record for user table
		var userRecord = new GlideRecord("sys_user");
		//use the sysparm_userID passed from the client side to retrieve a record from user table
		//The syntax this.getParameter('sysparm_<parameter_name>') means to get the value of the parameter passed in from the client-side script. In this example, sysparm_userID contains the sys_id of a User table record.
		userRecord.get(this.getParameter("sysparm_UserID"));
		// return a email address for the requested record
		return userRecord.email+'';

	},

    type: 'GetEmailAddress'
});
